note_models.css:
:root {
	--main-bg-color: #434343;
	--upper-bg-color: #555555;
	--lower-bg-color: #transparent;
	--upper-fg-color: #eeeeee;
	--lower-fg-color: #555555;
	--primary-color: #9370db;
}

html,
body {
	word-wrap: normal;
}

u {
	text-decoration-style: wavy;
	text-underline-position: under;
	color: white;
	padding: 1px;
}

.card {
	font-family: UD Digi Kyokasho N-R;
	font-size: 0px;
	text-align: center;
}

.stripe {
	height: 5px;
	background-color: var(--primary-color);
}

.kard {
	height: 100vh;
	background-color: var(--main-bg-color);
}

.body-upper {
	font-family: Noto Sans;
	font-size: 30px;
	color: var(--upper-fg-color);
	background-color: var(--upper-bg-color);
	padding: 30px 10px 30px;
	{
		{
			/Title
		}
	}
	border-radius: 0px 0px 0px 0px;
}

.body-lower {
	text-align: left;
	color: var(--lower-fg-color);
	color: var(--lower-bg-color);
	height: 50%;
	padding: 25px 10px 25px 10px;
	border-radius: 0px 0px 0px 0px;
}

.soundFront {
	position: absolute;
	top: 40px;
	right: 40px;
	font-size: 18px;
	content: url("play.png") {
		{
			/Title
		}
	}
	;
	width: 6%;
}

.notes {
	font-family: Noto Sans Regular;
	font-size: 20px;
	/*
  border: 2px dashed rgba(85, 85, 85, 0.75);
  padding: 10px;
  margin: 3px 20px 3px 20px;
  */
	text-align: center;
	font-weight: bold;
}

.extra_toggle {
	display: block;
	font-family: Noto Sans Regular;
	font-size: 15px;
	color: var(--upper-fg-color);
	background-color: var(--upper-bg-color);
	border-radius: 0px;
	padding: 5px;
	width: 50%;
	text-align: center;
	font-weight: 500;
	margin: auto;
	margin-top: 20px;
	transition: all 0.15s ease-out;
}

.extra_toggle:hover {
	filter: brightness(90%);
}

.extra {
	display: none;
	font-family: Noto Sans Regular;
	font-size: 15px;
	border: 3px dashed var(--primary-color);
	color: var(--upper-fg-color);
	padding: 20px;
	margin: 30px 20px 3px 20px;
	text-align: center;
	font-weight: 500;
}

.cloze {
	background-color: var(--primary-color);
}

.stripe.night_mode {
	background-color: var(--primary-color);
}

.kard.night_mode {
	background-color: var(--main-bg-color);
}

.body-upper.night_mode {
	color: var(--upper-fg-color);
	background-color: var(--upper-bg-color);
}

.body-lower.night_mode {
	color: var(--lower-fg-color);
	color: var(--lower-bg-color);
}

.cloze.night_mode {
	background-color: var(--primary-color);
}

