/* Font imports */
@import url('https://fonts.googleapis.com/css?family=Bellota+Text|Roboto+Condensed|Roboto:400,500,700,900|Space+Mono:400,400i,700,700i&display=swap');

* {  
  word-wrap: break-word;  
}

.card {
  background-color: #222;
  color: #eee;
}

ul {
  list-style-position: inside;
  padding-top: 10px;
}
ul li {
  margin: auto;
  margin-top: 10px;
  padding: 10px;
  width: 20%;
  padding-left: 10%;
  padding-right: 10%;
  display: table;
  background-color: #444;
  font-family: 'Roboto Condensed', sans-serif;
}

ol {
  list-style-position: inside;
  padding: 2%;
  padding-top: 0;
}

.word_homophone_container {
  margin: auto;
  margin-top: 2%;
  width: 100%;
  text-align: center;
  display: grid;
}

/* Word Header */
.word_header {
  display: flex;
  font-family: "Roboto", sans-serif;
  font-weight: bold;
  font-size: 50px;
  overflow: hidden;
  margin: auto;
}
.word_type {
  border: 3px solid #eee;
  border-left: none;
  padding: 5px;
  background-color: #eee;
  color: #222;
  text-align: center;
  margin: auto;
  margin-right: 0;
}
.word_word {
  border: 3px solid #eee;
  padding: 5px;
  vertical-align:top;
  text-align: center;
  margin: auto;
  margin-left: 0;
}

/* Short definition */
.shortdef_container {
  align: center;
}

/* Long definition */
.word_long_definition {
  margin: auto;
  width: 50%;
  background-color: transparent;
}

.word_long_definition_list_container {
  display: none;
  background-color: #333;
  font-family: 'Roboto Condensed', sans-serif;
}

.word_long_definition_list_container li {
  padding-top: 20px;
}

/* Long definition buttons */
.word_long_definition_toggle_button {
  margin: auto;
  width: 20%;
  padding: 1%;
  background-color: rgba(50, 50, 50, 1);
  border-radius: 25px;
  font-family: 'Bellota Text', cursive;
}

.word_long_definition_hide_button {
  display: none;
}

