{{#sing1}}
<div class="kard">
    <div class="stripe"></div>
    <div class="body-upper" style="border-radius: 5px 5px 0px 0px;">
        <b class="hightlight-1">{{Tense}}</b><br />
        <b id="pronoun" class="hightlight-2">1. Singular</b><br />
        <b class="hightlight-3">{{Infinitive}}</b><br />
    </div>
    <div class="body-lower">
        <div class="notes">
            <div id="answer">?</div>
        </div>
    </div>
</div>
<div id="pronouns">{{Pronouns}}</div>

<div id="tags" style="display: none;">{{Tags}}</div>

<script>
	/* Inject correct pronoun */
	pronouns = document.getElementById("pronouns").innerText.split(";");
    if (pronouns[0]) {
    	document.getElementById("pronoun").innerText = pronouns[0];
	}

	/* Set the primary color based on tag */
    var tags = document.getElementById("tags").innerText.split(" ");
    tags.forEach(function (tag) {
        var split = tag.split("::");
        if (split[0] == "Color") {
            var r = document.querySelector(":root");
            r.style.setProperty("--primary-color", split[1]);
        }
    });
	/* Play audio when clicking the respective element */
    function playAudio(fieldToPlay) {
        var elem = document.querySelector("#" + fieldToPlay + " .soundLink, #" + fieldToPlay + " .replaybutton");
        if (elem) {
            elem.click();
        }
    }
    /* Autoplay some elements */
    var elem = document.querySelector(".autoplay .soundLink, .autoplay .replaybutton");
    if (elem) {
        elem.click();
    }
</script>
{{/sing1}}

