{{#CreateReversed}}
<div class="hidden autoplay" id="AudioSentence">{{AudioSentence}}</div>
<div class="hidden" id="AudioSentenceTranslation">{{AudioSentenceTranslation}}</div>

<div class="kard">
    <div class="stripe"></div>
    <div class="body-upper">
        <div class="main-top" onClick="playAudio('AudioSentence')">{{Sentence}}</div>
        <div style="height: 5px;"></div>

        <div class="top-container" style="display: flex; justify-content: center; font-size: 75%; width: 100%;">
            {{#Level}}
            <div class="extra-top-left" style="">
                {{Level}}
            </div>
            {{/Level}} {{#CountryISO}}
            <div class="extra-top-right" style="">
                <img id="languageImg" />
            </div>
            {{/CountryISO}}
        </div>
    </div>

    <div class="body-lower">
        <div class="notes">
			{{#Picture}}
			<div>{{Picture}}</div>
			{{/Picture}}
            <div id="answer">?</div>
        </div>
    </div>
</div>

<div style="display: none;" id="languageText">{{CountryISO}}</div>

<div id="tags" style="display: none;">{{Tags}}</div>

<script>
	/* Set the primary color based on tag */
    var tags = document.getElementById("tags").innerText.split(" ");
    tags.forEach(function (tag) {
        var split = tag.split("::");
        if (split[0] == "Color") {
            var r = document.querySelector(":root");
            r.style.setProperty("--primary-color", split[1]);
        }
    });
	/* {{#CountryISO}}} */
    /* Convert the language field to a flag */
    var lang = document.getElementById("languageText").innerText;
    var flag = "_flag_" + lang + ".svg";
    document.getElementById("languageImg").src = flag;
	/* {{/CountryISO}}} */

    /* Play audio when clicking the respective element */
    function playAudio(fieldToPlay) {
        var elem = document.querySelector("#" + fieldToPlay + " .soundLink, #" + fieldToPlay + " .replaybutton");
        if (elem) {
            elem.click();
        }
    }
    /* Autoplay some elements */
    var elem = document.querySelector(".autoplay .soundLink, .autoplay .replaybutton");
    if (elem) {
        elem.click();
    }
</script>
{{/CreateReversed}}

