body {
    word-wrap: break-word;
    background-color: var(--main-bg-color);
    padding: 0;
    margin: 0;
}
u {
    text-decoration-style: wavy;
    text-underline-position: under;
    color: white;
    padding: 1px;
}

/* Hide the default play button anki provides */
.replay-button svg {
    display: none;
}
.hidden {
    display: none;
}
#answer {
    font-size: 35px;
}

/* Main skeleton of an anki card */
.card {
    font-family: UD Digi Kyokasho N-R;
    font-size: 0px;
    text-align: center;
}
/* Div around all of the visible content of the card */
.kard {
    height: 100%;
    background-color: var(--main-bg-color);
}
.kard.night_mode {
    background-color: var(--main-bg-color);
}
/* Stripe at the top of the card */
.stripe {
    height: var(--stripe-height);
    background-color: var(--primary-color);
}
.stripe.night_mode {
    background-color: var(--primary-color);
}

/* Upper part of the card (Question) */
.body-upper {
    font-family: Noto Sans;
    font-size: 30px;
    color: var(--upper-fg-color);
    background-color: var(--upper-bg-color);
    padding: 30px 10px 30px;
    border-radius: 0px 0px 0px 0px;
}
.body-upper.night_mode {
    color: var(--upper-fg-color);
    background-color: var(--upper-bg-color);
}
/* Lower part of the card (Answer) */
.body-lower {
    text-align: left;
    color: var(--lower-fg-color);
    height: 100%;
    padding: 25px 10px 25px 10px;
    border-radius: 0px 0px 0px 0px;
}
.body-lower.night_mode {
    color: var(--lower-fg-color);
    color: var(--lower-bg-color);
}

.notes {
    font-family: Noto Sans Regular;
    font-size: 20px;
    text-align: center;
    font-weight: bold;
}

/* Show extra information button */
.extra_toggle {
    display: block;
    font-family: Noto Sans Regular;
    font-size: 15px;
    color: var(--upper-fg-color);
    background-color: var(--upper-bg-color);
    border-radius: 0px;
    padding: 5px;
    width: 50%;
    text-align: center;
    font-weight: 500;
    margin: auto;
    margin-top: 20px;
    transition: all 0.15s ease-out;
}
.extra_toggle:hover {
    filter: brightness(90%);
}

/* Div containing extra information, hidden by default */
.extra {
    display: none;
    font-family: Noto Sans Regular;
    font-size: 15px;
    border: 3px dashed var(--primary-color);
    color: var(--upper-fg-color);
    padding: 20px;
    margin: 30px 20px 3px 20px;
    text-align: center;
    font-weight: 500;
}
/* Div containing mnemonics or notes, situated inside the extra div */
.mnemonic {
    display: block;
    margin-bottom: 2%;
    border-color: var(--secondary-color);
}

/* Contains extra elements at the top, e.g. flags */
.top-container {
    width: 100%;
    position: absolute;
    top: calc(var(--stripe-height) + var(--margin-from-stripe));
    left: 0;
}
.extra-top-right {
    position: absolute;
    right: var(--margin-from-stripe);
}
.extra-top-left {
    position: absolute;
    left: var(--margin-from-stripe);
}
.extra-top-right img {
    float: right;
    width: 55px;
    border-radius: 10%;
}
.extra-top-left img {
    float: left;
    width: 55px;
    border-radius: 10%;
}

/* Style cloze to use the primary color as the background color to make the cloze deletion easier to spot */
.cloze {
    background-color: var(--primary-color);
}
.cloze.night_mode {
    background-color: var(--primary-color);
}

/* Highlighting classes */
.hightlight-1 {
    background-color: var(--primary-color);
	display: inline;
}

.hightlight-2 {
    background-color: var(--secondary-color);
	display: inline;
}

.hightlight-3 {
    background-color: var(--tertiary-color);
	display: inline;
}

.hightlight-1.night_mode {
    background-color: var(--primary-color);
	display: inline;
}

.hightlight-2.night_mode {
    background-color: var(--secondary-color);
	display: inline;
}

.hightlight-3.night_mode {
    background-color: var(--tertiary-color);
	display: inline;
}

tmpls.qfmt:
{{#Date}}
<div class="kard">
    <div class="stripe"></div>
    <div class="body-upper">
        <b class="hightlight-1">{{Title}}</b> - Date of Publication
    </div>

    <div class="body-lower">
        <div class="notes">
            <b class="hightlight-3">?</b>
        </div>
    </div>
</div>

<div id="tags" style="display: none;">{{Tags}}</div>

<script>
	/* Set the primary color based on tag */
    var tags = document.getElementById("tags").innerText.split(" ");
    tags.forEach(function (tag) {
        var split = tag.split("::");
        if (split[0] == "Color") {
            var r = document.querySelector(":root");
            r.style.setProperty("--primary-color", split[1]);
        }
    });
    /* Play audio when clicking the respective element */
    function playAudio(fieldToPlay) {
        var elem = document.querySelector("#" + fieldToPlay + " .soundLink, #" + fieldToPlay + " .replaybutton");
        if (elem) {
            elem.click();
        }
    }
    /* Autoplay some elements */
    var elem = document.querySelector(".autoplay .soundLink, .autoplay .replaybutton");
    if (elem) {
        elem.click();
    }
</script>
{{/Date}}

